import icon from "../images/icon-success.svg";

export default function Success() {
  return (
    <div className="grid gap-16 py-[9.25rem] px-6 text-[#242742]">
      <img src={icon} alt="Success Icon" />
      <h1 className="font-bold text-4xl">Thanks for <br /> subscribing!</h1>
      <p>
        A confirmation email has been sent to ash@loremcompany.com. Please open
        it and click the button inside to confirm your subscription.
      </p>
      <button className="bg-[#242742] text-white font-bold">Dismiss message</button>
    </div>
  );
}
