import { useState } from "react";

export default function MailForm() {
  const [error, setError] = useState(false);



  return (
    <form onSubmit={submitForm} className="grid gap-2 mt-7" action="#" noValidate>
      <div className="flex justify-between">
        <label
          className="text-[0.6875rem] font-bold tracking-wider"
          htmlFor="mailForm"
        >
          Email address
        </label>
        {error && (
          <span className="text-[0.6875rem] font-bold tracking-wider text-[#ff6257]">
            Valid email required
          </span>
        )}
      </div>
      <input
        className={`border ${
          error ? "border-[#ff6257]" : "border-[#9294a0]"
        } rounded-lg py-[0.9375rem] pl-6 placeholder:text-[0.9375rem] placeholder:tracking-wide ${error && 'bg-[#ff62572c]' && 'text-[#ff6257]'}`}
        type="email"
        name="mailForm"
        id="mailForm"
        placeholder="email@company.com"
      />
      <input
        className="bg-[#242742] text-white font-bold mt-4 rounded-lg pt-[1.0625rem] pb-4 hover:bg-[#ff6257] hover:cursor-pointer"
        type="submit"
        value="Subscribe to monthly newsletter"
      />
    </form>
  );
}
